INSERT INTO `queryutente` VALUES (1,12,'elenco PC e numeri di serie','select id, descrizione,posizione, sn,coa, marca,modello,IDRete  from apparecchi',1),(2,12,'lista interventi','SELECT apparecchi.ID, TipiApparecchi.descrizione AS tipo, \r\n    Apparecchi.Descrizione, ultimomov.data, \r\n    ultimomov.evento\r\nFROM (SELECT cronologia.Apparecchio, cronologia.data, \r\n          cronologia.evento\r\n      FROM (SELECT MAX(cronologia.data) AS data, \r\n                cronologia.Apparecchio\r\n            FROM cronologia\r\n            GROUP BY apparecchio) data INNER JOIN\r\n          dbo.cronologia ON \r\n          data.data = dbo.cronologia.data AND \r\n          data.Apparecchio = dbo.cronologia.Apparecchio) \r\n    ultimomov INNER JOIN\r\n    dbo.Apparecchi ON \r\n    ultimomov.Apparecchio = dbo.Apparecchi.ID INNER JOIN\r\n    dbo.TipiApparecchi ON \r\n    dbo.Apparecchi.Tipo = dbo.TipiApparecchi.ID\r\norder by ultimomov.data',1),(3,12,'pc rackmount','select * from apparecchi where Apparecchi.Tipo= 17',1),(4,12,'lista apparecchi','select id_aziende,id, descrizione,posizione, sn,coa, marca,modello,IDRete  from apparecchi order by id_aziende',1),(5,12,'Numeri serie PC','select apparecchi.ID ,apparecchi.SN,  apparecchi.marca ,apparecchi.modello ,apparecchi.posizione ,apparecchi.Descrizione from apparecchi order by sn',1),(6,12,'lista  IP address','select Apparecchi.ID, Apparecchi.Descrizione, Apparecchi.IPAddress, Apparecchi.IDrete from apparecchi order by ipaddress ',1),(7,12,'lista versioni sw','select elencoSW.computer, software.Descrizione, elencoSW.Versione,  elencoSW.SN from elencosw,software where software.id=elencosw.software',1),(8,12,'PC senza indirizzo IP','select Apparecchi.ID, Apparecchi.Descrizione, Apparecchi.IPAddress, Apparecchi.IDrete,apparecchi.posizione  from apparecchi where  ipaddress = \'\' order by descrizione',1),(9,12,'Materiale andato a volvic','select id, descrizione, posizione from apparecchi where posizione like \'%volvic%\'',1),(10,12,'Lista materiale ancora in riparazione','select id, sn , descrizione, posizione from apparecchi where posizione like \'%riparazione%\'',1),(11,17,'Pc con word 2003','select distinct apparecchi.id ,posizione,descrizione from apparecchi,elencosw where\r\n(software like \'%2003%\' or\r\n versione like \'%2003%\') and  Apparecchi.ID= elencoSW.computer ;',1),(12,12,'Numero interventi per macchina','SELECT Apparecchi.Descrizione, Apparecchi.posizione, \r\n    Apparecchi.marca, Apparecchi.modello, COUNT(*) \r\n    AS Expr1\r\nFROM cronologia INNER JOIN\r\n    Apparecchi ON \r\n    cronologia.Apparecchio = Apparecchi.ID\r\nGROUP BY Apparecchi.Descrizione, Apparecchi.posizione, \r\n    Apparecchi.marca, Apparecchi.modello\r\n\r\norder by expr1',1),(13,12,'note apparecchi','select id,note from apparecchi where note is not null',1),(14,12,'elenco PC e software','select apparecchi.id, apparecchi.marca, apparecchi.modello, apparecchi.sn,apparecchi.coa,apparecchi.Datainst, funzpc.funzione, apparecchi.IPAddress,apparecchi.IDrete, software.ID, software.Descrizione, elencosw.Versione,if(elencosw.Licenza=1,\'ok\',\'\') as licenza\r\nfrom apparecchi left join elencosw on apparecchi.ID = elencosw.computer left join software on elencosw.software=software.ID \r\nleft outer join (select pc_funzioni.id_apparecchi, pc_tipifunzione.funzione\r\nfrom pc_funzioni inner join pc_tipifunzione on pc_funzioni.id_pc_tipifunzione = pc_tipifunzione.ID\r\nwhere pc_funzioni.ordine=1) funzpc on apparecchi.ID=funzpc.id_apparecchi \r\norder by apparecchi.ID, software.ID',1),(15,12,'riepilogo software','select  software.ID,  software.Descrizione,  elencosw.Licenza, count(elencosw.Computer) as numero\r\nfrom apparecchi left join elencosw on apparecchi.ID = elencosw.computer left join software on elencosw.software=software.ID\r\ngroup by  software.ID, software.Descrizione, elencosw.Licenza',1),(16,12,'pacchetti software per PC','select computer,  count(software) as conteggio  from elencosw group by computer',1),(17,12,'situazione software scorz','select apparecchi.id, apparecchi.marca as \'marca PC\', apparecchi.modello as \'modello PC\', apparecchi.sn as seriale, funzpc.funzione as \'funzione computer\', apparecchi.IPAddress as \'indirizzo IP\', software.Descrizione as software, elencosw.Versione, software.tipologia, if(elencosw.Licenza=1,\'ok\',\'\') as licenza\r\nfrom apparecchi left join elencosw on apparecchi.ID = elencosw.computer left join software on elencosw.software=software.ID \r\nleft outer join (select pc_funzioni.id_apparecchi, pc_tipifunzione.funzione\r\nfrom pc_funzioni inner join pc_tipifunzione on pc_funzioni.id_pc_tipifunzione = pc_tipifunzione.ID\r\nwhere pc_funzioni.ordine=1) funzpc on apparecchi.ID=funzpc.id_apparecchi \r\nwhere apparecchi.id_aziende=1\r\norder by apparecchi.ID, software.ID',1),(18,12,'Conteggio PC per funzione','select  pc_tipifunzione.funzione, count(pc_funzioni.id_apparecchi) as totale from pc_funzioni inner join pc_tipifunzione on pc_funzioni.id_pc_tipifunzione =  pc_tipifunzione.ID\r\ngroup by  pc_tipifunzione.funzione',1),(19,12,'Elenco PC per funzione','select  pc_tipifunzione.funzione, pc_funzioni.id_apparecchi, apparecchi.IDrete, apparecchi.IPAddress  from pc_funzioni inner join pc_tipifunzione on pc_funzioni.id_pc_tipifunzione =  pc_tipifunzione.ID\r\ninner join apparecchi on  apparecchi.ID = pc_funzioni.id_apparecchi \r\norder by pc_tipifunzione.funzione, apparecchi.IPAddress\r\n\r\n',1),(20,12,'software fuori licenza - scorze','select apparecchi.id, apparecchi.marca as \'marca PC\', apparecchi.modello as \'modello PC\', apparecchi.sn as seriale, funzpc.funzione as \'funzione computer\', apparecchi.IPAddress as \'indirizzo IP\', software.Descrizione as software, elencosw.Versione, software.tipologia from apparecchi left join elencosw on apparecchi.ID = elencosw.computer left join software on elencosw.software=software.ID left outer join (select pc_funzioni.id_apparecchi, pc_tipifunzione.funzione from pc_funzioni inner join pc_tipifunzione on pc_funzioni.id_pc_tipifunzione = pc_tipifunzione.ID where pc_funzioni.ordine=1) funzpc on apparecchi.ID=funzpc.id_apparecchi where apparecchi.id_aziende=1 and elencosw.Licenza <> 1 order by apparecchi.ID, software.ID',1),(21,12,'elenco riparazioni (grossolano)','select apparecchio, date_format(data,\'%d/%m/%Y\') as \'data evento\', evento from cronologia where evento like \'%riparazione%\' order by apparecchio,data',1);
